SET NAMES utf8;
SET time_zone = '+00:00';
SET foreign_key_checks = 0;
SET sql_mode = 'NO_AUTO_VALUE_ON_ZERO';

SET NAMES utf8mb4;

CREATE TABLE `countries` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name_en` varchar(255) NOT NULL,
  `name_ru` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `favorites` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `created` int(10) unsigned NOT NULL,
  `user_id` bigint(20) unsigned NOT NULL,
  `place_id` int(10) unsigned NOT NULL,
  `month` tinyint(2) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `user_id_place_id_month` (`user_id`,`place_id`,`month`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE `places` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name_en` varchar(255) NOT NULL,
  `name_ru` varchar(255) NOT NULL,
  `description_en` text NOT NULL,
  `description_ru` text NOT NULL,
  `country_id` tinyint(3) unsigned NOT NULL,
  `video` varchar(255) NOT NULL,
  `nature_city` tinyint(2) unsigned NOT NULL,
  `mountain_sea` tinyint(2) unsigned NOT NULL,
  `old_new` tinyint(2) unsigned NOT NULL,
  `photos` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `country_id` (`country_id`),
  KEY `nature_city_mountain_sea` (`nature_city`,`mountain_sea`),
  KEY `nature_city_old_new` (`nature_city`,`old_new`),
  KEY `country_id_nature_city_mountain_sea` (`country_id`,`nature_city`,`mountain_sea`),
  KEY `country_id_nature_city_old_new` (`country_id`,`nature_city`,`old_new`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `seasons` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `place_id` int(10) unsigned NOT NULL,
  `month` tinyint(2) unsigned NOT NULL,
  `season` tinyint(2) unsigned NOT NULL,
  `mini_description_en` text NOT NULL,
  `mini_description_ru` text NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `place_id_month` (`place_id`,`month`),
  KEY `month` (`month`),
  KEY `season` (`season`),
  KEY `place_id` (`place_id`)
) ENGINE=MyISAM AUTO_INCREMENT=363 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `created` int(10) unsigned NOT NULL,
  `user_id` bigint(20) unsigned NOT NULL,
  `platform` varchar(10) DEFAULT NULL,
  `language` varchar(2) DEFAULT NULL,
  `allows_write_to_pm` tinyint(1) unsigned zerofill DEFAULT NULL,
  `first_name` varchar(50) DEFAULT NULL,
  `last_name` varchar(50) DEFAULT NULL,
  `username` varchar(50) DEFAULT NULL,
  `is_premium` tinyint(1) unsigned zerofill DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `user_id` (`user_id`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
